package com.HomeAssigment.ex3;import java.util.*;public class Solution {    public int solution(int[] A) {        if (A == null || A.length < 2) {            throw new IllegalArgumentException("Array must contain at least two elements.");        }        Map<String, int[]> map = new HashMap<>();        for (int num : A) {            String numStr = Integer.toString(num);            if (numStr.isEmpty()) continue;            String key = numStr.charAt(0) + "," + numStr.charAt(numStr.length() - 1);            if (!map.containsKey(key)) {                map.put(key, new int[]{num, Integer.MIN_VALUE});            } else {                int[] topTwo = map.get(key);                if (num > topTwo[0]) {                    topTwo[1] = topTwo[0];                    topTwo[0] = num;                } else if (num > topTwo[1]) {                    topTwo[1] = num;                }            }        }        int maxSum = -1;        for (int[] topTwo : map.values()) {            if (topTwo[1] != Integer.MIN_VALUE) {                maxSum = Math.max(maxSum, topTwo[0] + topTwo[1]);            }        }        return maxSum;    }}