package com.HomeAssigment.ex2;public class PingPongManager {    private final Object lock = new Object();    private boolean isPing = true;    public void printPing() {        synchronized (lock) {            while (!isPing) {                try {                    lock.wait();                } catch (InterruptedException e) {                    return;                }            }            System.out.println("ping");            isPing = false;            lock.notify();        }    }    public void printPong() {        synchronized (lock) {            while (isPing) {                try {                    lock.wait();                } catch (InterruptedException e) {                    return;                }            }            System.out.println("pong");            isPing = true;            lock.notify();        }    }}