package com.HomeAssigment.ex2;public class PingPongTask implements Runnable {    private final PingPongManager manager;    private final boolean isPing;    private final long startTime;    private static final int DURATION = 5000;    public PingPongTask(PingPongManager manager, boolean isPing, long startTime) {        this.manager = manager;        this.isPing = isPing;        this.startTime = startTime;    }    @Override    public void run() {        while (System.currentTimeMillis() - startTime < DURATION) {            if (isPing) {                manager.printPing();            } else {                manager.printPong();            }        }    }}